namespace NumberTiles {
    const numberTiles: Tile[][] = [
        [null, null, null, null],
        [null, null, null, null],
        [null, null, null, null],
        [null, null, null, null]
    ];

    export function get(row: number, column: number): Tile {
        if (row < 0 || row > 3 || column < 0 || column > 3) {
            return null;
        }
        return numberTiles[row][column];
    }

    export function set(row: number, column: number, tile: Tile): boolean {
        numberTiles[row][column] = tile;
        return tile.set(row, column);
    }

    export function move(fromRow: number, fromColumn: number, toRow: number, toColumn: number): boolean {
            const changed = set(toRow, toColumn, get(fromRow, fromColumn));
            remove(fromRow, fromColumn);
            return changed;
    }

    export function remove(row: number, column: number) {
        numberTiles[row][column] = null;
    }

    export function createTile(row: number, column: number, n: number) {
        numberTiles[row][column] = new Tile(n, row, column);
    }

    export function createRandomTile() {
        let row = 0;
        let column = 0;
        do {
            row = randint(0, 3);
            column = randint(0, 3);
        } while (numberTiles[row][column]);

        createTile(row, column, Math.percentChance(10) ? 4 : 2);
    }

    export function combine(rowA: number, columnA: number, rowB: number, columnB: number): boolean {
        const tileA = get(rowA, columnA);
        const tileB = get(rowB, columnB);
        if (tileA && tileB && (tileA.n === tileB.n)) {
            // Combine them
            tileB.n *= 2;
            tileA.destroy();
            remove(rowA, columnA);
            return true;
        } else {
            return false;
        }
    }
}

function forEach(consume: (i: number, skipNext: (n: number) => number) => void) {
    for (let i = 0; i < 4; ++i) {
        consume(i, (n: number) => i += n);
    }
}

function forEachBackwards(consume: (i: number, skipNext: (n: number) => number) => void) {
    for (let i = 3; i >= 0; --i) {
        consume(i, (n: number) => i -= n);
    }
}


function move(direction: Direction, combine: boolean): boolean {
    let changed = false;

    if (direction === Direction.UP) {
        // Move the tiles up
        forEach((column) => {
            let holes = 0;
            forEach((row) => {
                if (NumberTiles.get(row, column)) {
                    if (holes && NumberTiles.move(row, column, row - holes, column)) {
                        changed = true;
                    }
                } else {
                    ++holes;
                }
            });
        });
        if (combine) {
            // Combine tiles with the same value
            forEach((column) => {
                forEachBackwards((row, skipNext) => {
                    if (NumberTiles.combine(row, column, row - 1, column)) {
                        skipNext(1);
                        changed = true;
                    }

                });
            });
            if (changed) {
                move(direction, false);
            }
        }

    } else if (direction === Direction.DOWN) {
        forEach((column) => {
            let holes = 0;
            forEachBackwards((row) => {
                if (NumberTiles.get(row, column)) {
                    if (holes && NumberTiles.move(row, column, row + holes, column)) {
                        changed = true;
                    }
                } else {
                    ++holes;
                }
            });
        });
        if (combine) {
            // Combine tiles with the same value
            forEach((column) => {
                forEach((row, skipNext) => {
                    if (NumberTiles.combine(row, column, row + 1, column)) {
                        skipNext(1);
                        changed = true;
                    }
                });
            });
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.LEFT) {
        forEach((row) => {
            let holes = 0;
            forEach((column) => {
                if (NumberTiles.get(row, column)) {
                    if (holes && NumberTiles.move(row, column, row, column - holes)) {
                        changed = true;
                    }
                } else {
                    ++holes;
                }
            });
        });
        if (combine) {
            // Combine tiles with the same value
            forEach((row) => {
                forEachBackwards((column, skipNext) => {
                    if (NumberTiles.combine(row, column, row, column - 1)) {
                        skipNext(1);
                        changed = true;
                    }
                });
            });
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.RIGHT) {
        forEach((row) => {
            let holes = 0;
            forEachBackwards((column) => {
                if (NumberTiles.get(row, column)) {
                    if (holes && NumberTiles.move(row, column, row, column + holes)) {
                        changed = true;
                    }
                } else {
                    ++holes;
                }
            });
        });
        if (combine) {
            // Combine tiles with the same value
            forEach((row) => {
                forEach((column, skipNext) => {
                    if (NumberTiles.combine(row, column, row, column + 1)) {
                        skipNext(1);
                        changed = true;
                    }
                });
            });
            if (changed) {
                move(direction, false);
            }
        }
    }

    return changed;
}

function isFull() {
    for (let column = 0; column < 4; ++column) {
        for (let row = 0; row < 4; ++row) {
            if (!NumberTiles.get(row, column)) {
                return false;
            }
        }
    }

    return true;
}

function doMove(direction: Direction) {
    const couldMove = move(direction, true);
    if (couldMove) {
        NumberTiles.createRandomTile();
    } else if (isFull()) {
        //game.over(false);
    }
}

