class Tile {
    public static readonly WIDTH = 28;
    private static readonly OFFSET_X = 26;
    private static readonly OFFSET_Y = 5;
    private _row: number;
    private _column: number;
    private _n: number;
    private sprite: Sprite;

    constructor(n: number, row: number, column: number) {
        const animationImage = image.create(Tile.WIDTH - 2, Tile.WIDTH - 2);
        this.sprite = sprites.create(animationImage);
        this.sprite.setPosition(Tile.OFFSET_X + column * (Tile.WIDTH - 1) + 14, Tile.OFFSET_Y + row * (Tile.WIDTH - 1) + 14)
        const frames = 4;
        const step = animationImage.height / frames;
        for (let i = 0; i < frames; i++) {
            animationImage.fillRect((animationImage.width - i * step) / 2, (animationImage.height - i * step) / 2, i * step, i * step, Tile.colorFor(n));
            pause(15);
        }
        this.n = n;
    }

    set n(n: number) { 
        this.sprite.setImage(Tile.createImage(n));
        this._n = n;
    }

    get n(): number {
        return this._n;
    }

    public destroy() {
        this.sprite.destroy();
        this.sprite = null;
    }

    public set(row: number, column: number): boolean {
        const expectedY = Tile.OFFSET_Y + row * (Tile.WIDTH - 1) + 14;
        const expectedX = Tile.OFFSET_X + column * (Tile.WIDTH - 1) + 14;
        if (row !== this._row || column !== this._column) {
            this.sprite.setPosition(expectedX, expectedY);
            this._row = row;
            this._column = column;
            return true;
        } else {
            return false;
        }
    }

    private static colorFor(n: number): number {
        const c: number = Math.round(tileBackground2Color + ((Math.log(n) / Math.log(2) - 0))) - 1;
        return c <= 15 ? c: fontDarkColor;
    }

    private static createImage(n: number): Image {
        const img = image.create(Tile.WIDTH - 2, Tile.WIDTH - 2);
        img.fill(Tile.colorFor(n));
        img.drawRect(0, 0, img.height, img.width, gridColor);
        const text = "" + n;
        const font = image.font5;
        let w = text.length * font.charWidth;
        let offset = (Tile.WIDTH - w) / 2;
        img.print(text, offset, (img.height - font.charHeight) / 2, n > 4 ? white : fontDarkColor, font)
        return img;
    }
}


function createTile(row: number, column: number, n: number) {
    numberTiles[row][column] = new Tile(n, row, column);
}

function createRandomTile() {
    let row = 0;
    let column = 0;
    do {
        row = randint(0, 3);
        column = randint(0, 3);
    } while (numberTiles[row][column]);

    createTile(row, column, Math.percentChance(10) ? 4 : 2);
}

function combineTiles(tileA: Tile, tileB: Tile, row: number, column: number): boolean {
    if (tileA && tileB && (tileA.n === tileB.n)) {
        // Combine them
        tileB.n *= 2;
        tileA.destroy();
        numberTiles[row][column] = null;
        return true;
    } else {
        return false;
    }
}

function move(direction: Direction, combine: boolean): boolean {
    let changed = false;

    if (direction === Direction.UP) {
        // Move the tiles up
        for (let column = 0; column < 4; column++) {
            let holes = 0;
            for (let row = 0; row < 4; row++) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        changed = numberTiles[row][column].set(row - holes, column) || changed;
                        numberTiles[row - holes][column] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let column = 0; column < 4; column++) {
                for (let row = 0; row < 4; row++) {
                    if (row - 1 >= 0) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row - 1][column];
                        changed = combineTiles(tileA, tileB, row, column) || changed;
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }

    } else if (direction === Direction.DOWN) {
        for (let column = 0; column < 4; column++) {
            let holes = 0;
            for (let row = 3; row >= 0; row--) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        changed = numberTiles[row][column].set(row + holes, column) || changed ;
                        numberTiles[row + holes][column] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let column = 0; column < 4; column++) {
                for (let row = 3; row >= 0; row--) {
                    if (row + 1 < 4) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row + 1][column];
                        changed = combineTiles(tileA, tileB, row, column) || changed;
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.LEFT) {
        for (let row = 0; row < 4; row++) {
            let holes = 0;
            for (let column = 0; column < 4; column++) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        changed = numberTiles[row][column].set(row, column - holes) || changed;
                        numberTiles[row][column - holes] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let row = 0; row < 4; row++) {
                for (let column = 0; column < 4; column++) {
                    if (column - 1 >= 0) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row][column - 1];
                        changed = combineTiles(tileA, tileB, row, column) || changed;
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.RIGHT) {
        for (let row = 0; row < 4; row++) {
            let holes = 0;
            for (let column = 3; column >= 0; column--) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        changed = numberTiles[row][column].set(row, column + holes) || changed;
                        numberTiles[row][column + holes] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let row = 0; row < 4; row++) {
                for (let column = 0; column < 4; column++) {
                    if (column + 1 < 4) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row][column + 1];
                        changed = combineTiles(tileA, tileB, row, column) || changed;
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    }

    return changed;
}

function isFull() {
    for (let column = 0; column < 4; column++) {
        for (let row = 0; row < 4; row++) {
            if (!numberTiles[row][column]) {
                return false;
            }
        }
    }

    return true;
}

function doMove(direction: Direction) {
    const couldMove = move(direction, true);
    if (couldMove) {
        createRandomTile();
    } else if (isFull()) {
        //game.over(false);
    }
}

