class Tile {
    public n: number;
    public sprite: Sprite;

    constructor(n: number, sprite: Sprite) {
        this.n = n;
        this.sprite = sprite;
    }
}

function randomNumber() {
    return Math.percentChance(10) ? 4 : 2;
}

function colorFor(n: number): number {
    const c: number = Math.round(tileBackground2Color + ((Math.log(n) / Math.log(2) - 0))) - 1;
    return c <= 15 ? c: fontDarkColor;
}

function createImage(n: number): Image {
    const img = image.create(width - 2, width - 2);
    img.fill(colorFor(n));
    img.drawRect(0, 0, img.height, img.width, gridColor);
    const text = "" + n;
    const font = image.font5;
    let w = text.length * font.charWidth;
    let offset = (width - w) / 2;
    img.print(text, offset, (img.height - font.charHeight) / 2, n > 4 ? white : fontDarkColor, font)
    return img;
}

function createTile(row: number, column: number, n: number) {
    const animationImage = image.create(width - 2, width - 2);
    const s = sprites.create(animationImage);
    s.setPosition(offsetX + column * (width - 1) + 14, offsetY + row * (width - 1) + 14)
    const frames = 4;
    const step = animationImage.height / frames;
    for (let i = 0; i < frames; i++) {
        animationImage.fillRect((animationImage.width - i * step) / 2, (animationImage.height - i * step) / 2, i * step, i * step, colorFor(n));
        pause(15);
    }
    s.setImage(createImage(n));
    numberTiles[row][column] = new Tile(n, s);
}

function createRandomTile() {
    let row = 0;
    let column = 0;
    do {
        row = randint(0, 3);
        column = randint(0, 3);
    } while(numberTiles[row][column]);

    createTile(row, column, randomNumber());
}

function move(direction: Direction, combine: boolean): boolean {
    let changed = false;

    if (direction === Direction.UP) {
        // Move the tiles up
        for (let column = 0; column < 4; column++) {
            let holes = 0;
            for (let row = 0; row < 4; row++) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        numberTiles[row - holes][column] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let column = 0; column < 4; column++) {
                for (let row = 0; row < 4; row++) {
                    if (row - 1 >= 0) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row - 1][column];
                        if (tileA && tileB && (tileA.n === tileB.n)) {
                            // Combine them
                            tileB.sprite.setImage(createImage(tileB.n * 2));
                            tileB.n *= 2;
                            tileA.sprite.destroy();
                            numberTiles[row][column] = null;
                            changed = true;
                        }
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }

    } else if (direction === Direction.DOWN) {
        for (let column = 0; column < 4; column++) {
            let holes = 0;
            for (let row = 3; row >= 0; row--) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        numberTiles[row + holes][column] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let column = 0; column < 4; column++) {
                for (let row = 3; row >= 0; row--) {
                    if (row + 1 < 4) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row + 1][column];
                        if (tileA && tileB && (tileA.n === tileB.n)) {
                            // Combine them
                            tileB.sprite.setImage(createImage(tileB.n * 2));
                            tileB.n *= 2;
                            tileA.sprite.destroy();
                            numberTiles[row][column] = null;
                            changed = true;
                        }
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.LEFT) {
        for (let row = 0; row < 4; row++) {
            let holes = 0;
            for (let column = 0; column < 4; column++) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        numberTiles[row][column - holes] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let row = 0; row < 4; row++) {
                for (let column = 0; column < 4; column++) {
                    if (column - 1 >= 0) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row][column - 1];
                        if (tileA && tileB && (tileA.n === tileB.n)) {
                            // Combine them
                            tileB.sprite.setImage(createImage(tileB.n * 2));
                            tileB.n *= 2;
                            tileA.sprite.destroy();
                            numberTiles[row][column] = null;
                            changed = true;
                        }
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    } else if (direction === Direction.RIGHT) {
        for (let row = 0; row < 4; row++) {
            let holes = 0;
            for (let column = 3; column >= 0; column--) {
                if (numberTiles[row][column]) {
                    if (holes) {
                        numberTiles[row][column + holes] = numberTiles[row][column];
                        numberTiles[row][column] = null;
                    }
                } else {
                    ++holes;
                }
            }
        }
        if (combine) {
            // Combine tiles with the same value
            for (let row = 0; row < 4; row++) {
                for (let column = 0; column < 4; column++) {
                    if (column + 1 < 4) {
                        const tileA = numberTiles[row][column];
                        const tileB = numberTiles[row][column + 1];
                        if (tileA && tileB && (tileA.n === tileB.n)) {
                            // Combine them
                            tileB.sprite.setImage(createImage(tileB.n * 2));
                            tileB.n *= 2;
                            tileA.sprite.destroy();
                            numberTiles[row][column] = null;
                            changed = true;
                        }
                    }
                }
            }
            if (changed) {
                move(direction, false);
            }
        }
    }

    // move the sprites if necessary
    for (let column = 0; column < 4; column++) {
        for (let row = 0; row < 4; row++) {
            if (!numberTiles[row][column]) continue;
            const tile = numberTiles[row][column];
            const expectedY = offsetY + row * (width - 1) + 14;
            const expectedX = offsetX + column * (width - 1) + 14;
            const xChanged = tile.sprite.x != expectedX;
            const yChanged = tile.sprite.y != expectedY;
            if (xChanged || yChanged) {
                tile.sprite.setPosition(expectedX, expectedY);
                changed = true;
            }
        }
    }

    return changed;
}

function doMove(direction: Direction) {
    if (move(direction, true)) {
        createRandomTile();
    }
}

